import React, { useEffect, useState } from 'react';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function MyOrder() {
    const [orderData, setOrderData] = useState([]);

    useEffect(() => {
        const fetchMyOrder = async () => {
            try {
                const response = await fetch("http://localhost:5000/api/myOrderData", {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ email: localStorage.getItem('userEmail') })
                });

                if (!response.ok) throw new Error('Failed to fetch orders');

                const data = await response.json();
                setOrderData(data);
            } catch (error) {
                console.error("Error fetching orders:", error);
            }
        };

        fetchMyOrder();
    }, []);

    if (!Array.isArray(orderData) || orderData.length === 0) return <p className="text-center mt-5">No orders found</p>;

    return (
        <>
            <Navbar />
            <div className='container'>
                <div className='row'>
                    {orderData.map((data, index) =>
                        data.orderData ? data.orderData.order_data.slice(0).reverse().map((items, i) =>
                            items.map((arrayData, j) => (
                                <div key={`${index}-${i}-${j}`}>
                                    {arrayData.Order_date ? (
                                        <div className='m-auto mt-5'>
                                            <strong>{arrayData.Order_date}</strong>
                                            <hr />
                                        </div>
                                    ) : (
                                        <div className='col-12 col-md-6 col-lg-3'>
                                            <div className="card mt-3" style={{ width: "16rem", maxHeight: "360px" }}>
                                                <img src={arrayData.img} className="card-img-top" alt="Order" style={{ height: "120px", objectFit: "fill" }} />
                                                <div className="card-body">
                                                    <h5 className="card-title">{arrayData.name}</h5>
                                                    <div className='container w-100 p-0' style={{ height: "38px" }}>
                                                        <span className='m-1'>{arrayData.qty}</span>
                                                        <span className='m-1'>{arrayData.size}</span>
                                                        <div className='d-inline ms-2 h-100 w-20 fs-5'>
                                                            ₹{arrayData.price}/-
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    )}
                                </div>
                            ))
                        ) : null
                    )}
                </div>
            </div>
            <Footer />
        </>
    );
}
