import React, { useEffect, useState } from 'react';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function MyOrder() {
    const [orderData, setOrderData] = useState([]); // Ensure it's an array

    const fetchMyOrder = async () => {
        try {
            const email = localStorage.getItem('userEmail');
            console.log("Fetching orders for:", email);
    
            const response = await fetch("http://localhost:5000/api/myOrderData", {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email })
            });
    
            if (!response.ok) {
                throw new Error('Failed to fetch orders');
            }
    
            const data = await response.json();
            console.log("ðŸ”¹ Full API Response:", data);
    
            // Extract and format the order data
            const cleanedOrders = data.orderData.map(order => {
                const orderDate = order[0].Order_data; // Extract order date
                const items = order.slice(1); // Remove the date object from the items
                return { orderDate, items }; // Return structured data
            });
    
            console.log("âœ… Orders with Date:", cleanedOrders); // Debugging
            setOrderData(cleanedOrders);
        } catch (error) {
            console.error("Error fetching orders:", error);
        }
    };
    
    
    useEffect(() => {
        fetchMyOrder();
    }, []);

    return (
        <>
            <Navbar />
            <div className="container mt-4">
                <h2>My Orders</h2>
    
                {/* âœ… Show message if no orders exist */}
                {orderData.length === 0 ? (
                    <p>No orders found.</p>
                ) : (
                    <div className="row">
                        {orderData.map((item, index) => (
                            <div key={index} className="col-md-4">
                                <div className="card mt-3" style={{ width: "18rem" }}>
                                     
                                    <div className="card-body">
                                        <h5 className="card-title">{item.name}</h5>
                                        <p>Quantity: {item.qty}</p>
                                        <p>Size: {item.size}</p>
                                        <p>Price: â‚¹{item.price}/-</p>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>
            <Footer />
        </>
    );
    
}
