import React, { useEffect, useState } from 'react';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function MyOrder() {
    const [orderData, setOrderData] = useState([]); // Ensure it's an array

    const fetchMyOrder = async () => {
        try {
            const email = localStorage.getItem('userEmail');
            console.log("Fetching orders for:", email);

            const response = await fetch("http://localhost:5000/api/auth/myOrderData", {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email })
            });

            if (!response.ok) {
                throw new Error('Failed to fetch orders');
            }

            const data = await response.json();
            console.log("Fetched order data:", data); // Debugging: See API response

            // Ensure orderData exists and is an array
            setOrderData(Array.isArray(data.orderData) ? data.orderData : []);
        } catch (error) {
            console.error("Error fetching orders:", error);
        }
    };

    useEffect(() => {
        fetchMyOrder();
    }, []);

    return (
        <>
            <Navbar />
            <div className="container mt-4 text-black">
                

                {orderData.length === 0 ? (
                    <p>No orders found.</p>
                ) : (
                    <div className="row">
                        {orderData.map((order, index) => {
                            console.log("Checking each order:", order); // Debugging

                            // Ensure order_data exists and has items
                            if (!order.order_data || order.order_data.length === 0) {
                                return <p key={index}>No items in this order.</p>;
                            }

                            return order.order_data.slice(0).reverse().map((items, i) =>
                                Array.isArray(items) && items.length > 0 ? (
                                    items.map((item, j) => (
                                        <div key={`${index}-${i}-${j}`} className="col-md-4">
                                            <div className="card mt-3" style={{ width: "18rem" }}>
                                                <img src={item.img} className="card-img-top" alt={item.name} style={{ height: "120px", objectFit: "cover" }} />
                                                <div className="card-body">
                                                    <h5 className="card-title">{item.name}</h5>
                                                    <p>Quantity: {item.qty}</p>
                                                    <p>Size: {item.size}</p>
                                                    <p>Price: â‚¹{item.price}/-</p>
                                                </div>
                                            </div>
                                        </div>
                                    ))
                                ) : (
                                    <p key={`${index}-${i}`}>This order has no items.</p>
                                )
                            );
                        })}
                    </div>
                )}
            </div>
            <Footer />
        </>
    );
}
