import React, { useEffect, useState } from 'react';
import Footer from '../components/Footer';
import Navbar from '../components/Navbar';

export default function MyOrder() {
    const [orderData, setOrderData] = useState([]);

    const fetchMyOrder = async () => {
        try {
            const response = await fetch("http://localhost:5000/auth/api/myOrderData", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: localStorage.getItem('userEmail')
                })
            });

            const result = await response.json();
            setOrderData(result.orderData || []);
        } catch (error) {
            console.error("Error fetching order data:", error);
        }
    };
//call fetchorder by useeffect
    useEffect(() => {
        fetchMyOrder();
    }, []);

    return (
        <div>
            <Navbar />
            <div className='container'>
                <div className='row'>
                    {orderData.length > 0 ? (
                        orderData.map((order, index) => (
                            <div key={index} className="mb-4 w-100">
                                <h4 className="text-center mt-4">{new Date(order[1]).toLocaleDateString()}</h4>
                                <hr />
                                <div className="row">
                                    {order[0].map((item, idx) => (
                                        <div key={idx} className='col-12 col-md-6 col-lg-3'>
                                            <div className="card mt-3" style={{ width: "16rem", maxHeight: "360px" }}>
                                                <img src={item.img} className="card-img-top" alt={item.name} style={{ height: "120px", objectFit: "cover" }} />
                                                <div className="card-body">
                                                    <h5 className="card-title">{item.name}</h5>
                                                    <div className='container w-100 p-0' style={{ height: "38px" }}>
                                                        <span className='m-1'>Qty: {item.qty}</span>
                                                        <span className='m-1'>Size: {item.size}</span>
                                                        <div className='d-inline ms-2 h-100 w-20 fs-5'>
                                                            â‚¹{item.price}/-
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        ))
                    ) : (
                        <h5 className="text-center mt-4">No orders found</h5>
                    )}
                </div>
            </div>
            <Footer />
        </div>
    );
}
