import React from 'react';
import { Link } from 'react-router-dom';

export default function Footer() {
  return (
    <footer className="bg-gradient-to-r from-purple-700 via-indigo-700 to-blue-700 text-white shadow-2xl rounded-t-2xl mt-8">
      <div className="max-w-7xl mx-auto px-6 py-6 flex flex-col md:flex-row items-center justify-between gap-4">
        
        {/* Logo and Copyright */}
        <div className="flex items-center space-x-4">
          <Link to="/" className="text-2xl font-bold hover:scale-105 transition-transform duration-300">
            SmartBite üçΩÔ∏è
          </Link>
          <span className="text-sm md:text-base opacity-80">¬© 2025 SmartBite. All rights reserved.</span>
        </div>

        {/* Navigation Links */}
        <div className="flex space-x-6 text-sm md:text-base">
          <Link to="/about" className="hover:text-gray-200 transition duration-300">About</Link>
          <Link to="/menu" className="hover:text-gray-200 transition duration-300">Menu</Link>
          <Link to="/contact" className="hover:text-gray-200 transition duration-300">Contact</Link>
        </div>
      </div>
    </footer>
  );
}
